package algorithms.memoization;
/*
https://www.geeksforgeeks.org/longest-common-subsequence-dp-4/
https://www.youtube.com/watch?v=sSno9rV8Rhg
 */
import java.util.Scanner;

public class Longest_Sub_Squence {
    // Complete the commonChild function below.
    static int commonChild(String s1, String s2, int n1, int n2, int[][] mem) {
        int result;
        if( n1 < 0 || n2 < 0 )
            return 0;

        if( mem[n1][n2] != -1)
            return mem[n1][n2];

        if( s1.charAt(n1) == s2.charAt(n2) )
            result =  1 + commonChild(s1, s2, n1-1, n2-1, mem);
        else
            result = maxSubSequence( commonChild(s1, s2, n1, n2-1, mem),
                    commonChild(s1, s2, n1-1, n2, mem));
        mem[n1][n2] = result;

        return result;
    }

    static int maxSubSequence( int max1, int max2){
        return ( max1 >= max2) ? max1 : max2;
    }


    private static final Scanner scanner = new Scanner(System.in);

    public static void main(String[] args){
        String s1 = "";

        String s2 = "";

        int[][] mem = new int[s1.length()][s2.length()];

        for(int i=0; i<mem.length; i++){
            for(int j=0; j<s2.length(); j++){
                mem[i][j] = -1;
            }
        }

        int result = commonChild(s1, s2, s1.length()-1, s2.length()-1, mem);

        System.out.println( result );


    }
}
